import React, { Component } from "react";
import logo from "../assets/hoaxify.png";
import { Link } from "react-router-dom";
import { withTranslation } from "react-i18next";

import LanguageSelector from "../components/LanguageSelector";
class TopBar extends Component {
  state = {
    isLoggedIn: true,
    username:'user1'
  };

  render() {
    const { t } = this.props;
    const { isLoggedIn, username } = this.state;

    let links = (
      <ul className="nav navbar-nav ml-auto">
        <li>
          <Link className="nav-link" to="/login">
            {t("Login")}
          </Link>
        </li>
        <li>
          <Link className="nav-link" to="/signup">
            {t("Sign Up")}
          </Link>
        </li>
        <li>
          <LanguageSelector />
        </li>
      </ul>
    );
    if (isLoggedIn) {
      links = (
        <ul className="nav navbar-nav ml-auto">
          <li>
            <Link className="nav-link" to={"/user/"+username}></Link>
          </li>
          <li className="nav-link">{t("Logout")}</li>
          <li>
            <LanguageSelector />
          </li>
        </ul>
      );
    }

    return (
      <div className="container-fluid shadow rounded   bg-primary mb-2">
        <nav className=" navbar navbar-dark container navbar-expand">
          <Link className="navbar-brand" to="/">
            <img src={logo} width="70" alt="Hoaxify Logo" />
            Hoaxify
          </Link>
          {links}
        </nav>
      </div>
    );
  }
}

export default withTranslation()(TopBar);
