import React from "react";
import { signup, changeLanguage } from "../api/apiCalls";
import Input from "../components/Input";
import { withTranslation } from 'react-i18next';
class UserLoginPage extends React.Component{
    state = {
        username: null,
        displayName: null,
        password: null,
        passwordRepeat: null,
        pendingApiCall: false,
        errors: {},
      };

    render(){
        return (
            <div className="container">
              <form>
                <h1 className="text-center ">{this.props.t('Login')}</h1>
                <Input
                  name="username"
                  label={this.props.t('Username')}
                  error={username}
                  onChange={this.onChange}
                />
               
                <Input
                  name="password"
                  label={this.props.t('Password')}
                  type="password"
                  error={password}
                  onChange={this.onChange}
                />
              
      
                <div className="text-center">
                  <button
                    className="btn btn-primary"
                    onClick={this.onClickLogin}
                    disabled={pendingApiCall || passwordRepeat !== undefined}
                  >
                    {this.props.t('Sign Up')}
                    {pendingApiCall && <i class="bi bi-box-arrow-in-right"></i>}
                  </button>
                </div>
                <div>
                <img src="https://www.countryflags.io/tr/flat/24.png" alt="Tukish Flag" onClick={() => this.onChangeLanguage('tr')}/>
                <img src="https://www.countryflags.io/us/flat/24.png" alt="American Flag" onClick={() => this.onChangeLanguage('en')}/>
                </div>
              </form>
            </div>
          );
    }
}


const UserLoginPageWithTranslation = withTranslation()(UserLoginPage);

export default UserLoginPageWithTranslation;
