import React, { Component } from "react";
import Input from "../components/Input";
import { withTranslation } from 'react-i18next';
import { login } from '../api/apiCalls';
class UserLoginPage extends Component {
    state = {
        username: null,
        password: null
    }

    onChange = event =>{
        const {name, value} = event.target;
        this.setState({
            [name]: value
        })
    }

    onClickLogin = event =>{
        event.preventDefault();
        const{ username, password} = this.state;
        const creds ={
            username,
            password 
        }
        login(creds)
    }

    render() {
    const {t} = this.props;
    return (
      <div className="container">
        <form>
          <h1 className="text-center">{t('Login')}</h1>
          <Input label={t("Username")} name="username" onChange={this.onChange} />
          <div></div>
          <Input label={t("Password")} name="username" type="password" onChange={this.onChange}/>
          <div></div>
          <div className="text-center">
            <button className="btn btn-primary">{t('Login')}</button>
          </div>
        </form>
      </div>
    );
  }
}


export default withTranslation()(UserLoginPage);
