import React, { useState } from "react";
import Input from "../components/Input";
import { withTranslation } from "react-i18next";
import ButtonWithProgress from "../components/ButtonWithProgress";
import { withApiProgress } from "../shared/ApiProgress";
import { connect } from "react-redux";
import { signupHandler } from "../redux/authActions";

const UserSignupPage = (props) => {
  const [form, setForm] = useState({
    username: null,
    displayName: null,
    password: null,
    passwordRepeat: null,
  });
  const [errors, setErrors] = useState({});


  const onChange = (event) => {
    const { t } = props;
    const { name, value } = event.target;
    const errorsCopy = { ...errors };
    errorsCopy[name] = undefined;

    if (name === "password" || name === "passwordRepeat") {
      if (name === "password" && value !== form.passwordRepeat) {
        errorsCopy.passwordRepeat = "Password mismatch";
      } else if (name === "passwordRepeat" && value !== form.password) {
        errorsCopy.passwordRepeat = "Password mismatch";
      } else {
        errorsCopy.passwordRepeat = undefined;
      }
    }
    setErrors(errorsCopy);
    const formCopy ={...form};
    formCopy[name] = value;
    setForm(formCopy);
    // this.setState({
    //   [name]: value,
    //   errors,
    // });
  };

  const onClickSignUp = async (event) => {
    event.preventDefault();
    const { history, dispatch } = props;
    const { push } = history;

    const { username, displayName, password } = form;

    const body = {
      username,
      displayName,
      password,
    };
    try {
      await dispatch(signupHandler(body));
      push("/");
    } catch (error) {
      if (error.response.data.validationErrors) {
        setErrors(error.response.data.validationErrors);
        // this.setState({ errors: error.response.data.validationErrors });
      }
    }
  };

  const { t, pendingApiCall } = props;
  // const { errors } = this.state;
  const {
    username: usernameError,
    displayName: displayNameError,
    password: passwordError,
    passwordRepeat: passwordRepeatError,
  } = errors;
  return (
    <div className="container">
      <form>
        <h1 className="text-center ">{props.t("Sign Up")}</h1>
        <Input
          name="username"
          label={props.t("Username")}
          error={usernameError}
          onChange={onChange}
        />
        <Input
          name="displayName"
          label={t("Display Name")}
          error={displayNameError}
          onChange={onChange}
        />
        <Input
          name="password"
          label={t("Password")}
          type="password"
          error={passwordError}
          onChange={onChange}
        />
        <Input
          name="passwordRepeat"
          label={t("Password Repeat")}
          type="password"
          error={passwordRepeatError}
          onChange={onChange}
        />

        <div className="text-center">
          <ButtonWithProgress
            onClick={onClickSignUp}
            disabled={pendingApiCall || passwordRepeatError !== undefined}
            pendingApiCall={pendingApiCall}
            text={t("Sign Up")}
          />
        </div>
      </form>
    </div>
  );
};

const UserSignupPageWithTranslation = withTranslation()(UserSignupPage);
const UserSignupPageWithApiProgressForSignupRequest = withApiProgress(
  UserSignupPageWithTranslation,
  "/api/1.0/users"
);
const UserSignupPageWithApiProgressForAuthRequest = withApiProgress(
  UserSignupPageWithApiProgressForSignupRequest,
  "/api/1.0/auth"
);

export default connect()(UserSignupPageWithApiProgressForAuthRequest);
