import React, { Component } from "react";
import { getUsers } from "../api/apiCalls";
import { withTranslation } from "react-i18next";
import UserListItem from "./UserListItem";
class UserList extends Component {
  state = {
    page: {
      content: [],
      size: 3,
      number: 0,
    },
  };

  componentDidMount() {
    getUsers().then((response) => {
      this.setState({
        page: response.data,
      });
    });
  }

  onClickNext = () => {
    const nextPage = this.state.page.number + 1;
    getUsers(nextPage).then((response) => {
      this.setState({
        page: response.data,
      });
    });
  };

  render() {
    const { t } = this.props;
    const { content: users, last, first } = this.state.page;
    return (
      <div className="card">
        <h3 className="card-header text-center">{t("Users")}</h3>
        <div className="list-group-flush">
          {users.map((user) => (
            <UserListItem key={user.username} user={user} />
          ))}
        </div>
        <div>
          {last === false && (
            <button className="btn btn-sm bg-secondary text-white font-weight-bold rounded-pill shadow-sm">
              Next
            </button>
          )}
        </div>
      </div>
    );
  }
}

export default withTranslation()(UserList);
