  //   }

  const onClickPrevious = () => {
    // const previousPage = this.state.page.number - 1;
    loadUsers(previousPage);
  };
  const onClickNext = () => {
    // const nextPage = this.state.page.number + 1;
    loadUsers(nextPage);
  };

  const loadUsers = (page) => {
    getUsers(page).then((response) => {
      // setState({
      //   page: response.data,
      // });
    });
  };

  const { t } = useTranslation;
  const { content: users, last, first } = this.state.page;
  return (
    <div className="card">
      <h3 className="card-header text-center">{t("Users")}</h3>
      <div className="list-group-flush">
        {users.map((user) => (
          <UserListItem key={user.username} user={user} />
        ))}
      </div>
      <div>
        {first === false && (
          <button
            className="btn btn-sm bg-secondary text-white font-weight-bold rounded-pill shadow-sm"
            onClick={onClickPrevious}
          >
            {t("Previous")}
          </button>
        )}
        {last === false && (
          <button
            className="btn btn-sm bg-secondary text-white font-weight-bold rounded-pill shadow-sm float-right "
            onClick={onClickNext}
          >
            {t("Next")}
          </button>
        )}
      </div>
    </div>
  );
};

export default UserList;
