import React from "react";

const ButtonWithProgress = (props) => {
  const { onClick, pendingApiCall, disabled, text } = props;
  return (
    <button className="btn btn-primary" style={{margin:'3'}} onClick={onClick} disabled={disabled}>
      {text}
      {pendingApiCall && <div class="spinner-grow text-light" role="status" />}
    </button>
  );
};

export default ButtonWithProgress;
