import React, { Component } from "react";
import { getUsers } from "../api/apiCalls";
import { withTranslation } from "react-i18next";
import UserListItem from "./UserListItem";

const UserList = () => {
  state = {
    page: {
      content: [],
      size: 3,
      number: 0,
    },
  };

//  componentDidMount() {
//     this.loadUsers();
//   }
 
  onClickPrevious = () => {
    const previousPage = this.state.page.number -1 ;
    this.loadUsers(previousPage);
  };
  onClickNext = () => {
    const nextPage = this.state.page.number + 1;
    this.loadUsers(nextPage);
  };

  loadUsers = (page) => {
    getUsers(page).then((response) => {
      this.setState({
        page: response.data,
      });
    });
  };

  const { t } = this.props;
  const { content: users, last, first } = this.state.page;
  return (
    <div className="card">
      <h3 className="card-header text-center">{t("Users")}</h3>
      <div className="list-group-flush">
        {users.map((user) => (
          <UserListItem key={user.username} user={user} />
        ))}
      </div>
      <div>
        {first === false && (
          <button
            className="btn btn-sm bg-secondary text-white font-weight-bold rounded-pill shadow-sm"
            onClick={this.onClickPrevious}
          >
            {t("Previous")}
          </button>
        )}
        {last === false && (
          <button
            className="btn btn-sm bg-secondary text-white font-weight-bold rounded-pill shadow-sm float-right "
            onClick={this.onClickNext}
          >
            {t("Next")}
          </button>
        )}
      </div>
    </div>
  );
}

export default withTranslation()(UserList);
